import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import AimGame from "./components/aimGame";
import Home from "./components/home";
import "./App.css";

class App extends React.Component {
  state = {
    tab: { aim: false, reflex: false, home: true }
  };
  handleTabChange = ({ target: { id } }) =>
    this.setState({
      tab: {
        aim: id === "aim",
        reflex: id === "reflex",
        home: id === "home"
      }
    });
  render() {
    const {
      tab: { home, reflex, aim }
    } = this.state;
    return (
      <div className="App">
        <header></header>
        <section
          style={{
            width: "100%",
            textAlign: "center",
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          {home ? (
             <Home />
          ) : (
            <div
              className="home-back"
              id={"home"}
              onClick={this.handleTabChange}
            >
              back{" "}
            </div>
          )}
          {aim ? <AimGame /> : ""}
        </section>
      </div>
    )
  }
}

export default App;
