import React, {Component} from 'react';
import { withRouter } from 'react-router-dom';
import {app, database } from '../base';

const Signup = ({ history }) => {

    const handleSubmit = async (e) => {
        console.log('ddddddddd')
         e.preventDefault();
         const {email, password} = e.target.elements;
         console.log(email)
        try {
            await app
            .auth()
            .createUserWithEmailAndPassword(email.value, password.value)
            .then(user =>{
                app.firestore().collection('users').doc(user.uid).set({
                    email,
                    score:0,
                })
            })
            .then(e => {
                console.log('user sign in ', e)
            })
            console.log('fadi asd')
            history.push("/");
        } catch(e) {
            console.log(e,'error in sign up');
        }
     }
        return ( 
            <div>
                <img alt="image" src="/home/fadi/Desktop/code/bubble-hunt/assests/BUBBLESHOOTER_BG.png"/>
                <form  onSubmit={handleSubmit} >
                    <label>
                        Email
                    <input type="text" name ="email" placeholder="enter your name" />
                    </label>
                    <label>
                        password
                    <input type="password" name="password" placeholder="enter your name" />
                    </label>
                    <button type="submit" name="signup">Signup</button>
                </form>
            </div>
         );
    }

export default withRouter(Signup);
